		{
		}
		treeBG3 = RollRandomForestBGStyle(random);
		while (treeBG3 == treeBG1 || treeBG3 == treeBG2)
		{
			treeBG3 = RollRandomForestBGStyle(random);
		}
		treeBG4 = RollRandomForestBGStyle(random);
		while (treeBG4 == treeBG1 || treeBG4 == treeBG2 || treeBG4 == treeBG3)
		{
			treeBG4 = RollRandomForestBGStyle(random);
		}
		setBG(0, treeBG1);
		setBG(10, treeBG2);
