	private static Point GetAdjustedFloorPosition(int x, int y)
	{
		int num = x - 1;
		int num2 = y - 2;
		bool isEmpty = false;
		bool hasFloor = false;
		while (!isEmpty && num2 > Main.spawnTileY - 10)
		{
			Scan3By3(num, num2, out isEmpty, out hasFloor);
			if (!isEmpty)
			{
				num2--;
			}
		}
		while (!hasFloor && num2 < Main.spawnTileY + 10)
		{
			Scan3By3(num, num2, out isEmpty, out hasFloor);
			if (!hasFloor)
			{
				num2++;
			}
		}
		return new Point(num + 1, num2 + 2);
	}

	private static void Scan3By3(int topLeftX, int topLeftY, out bool isEmpty, out bool hasFloor)
	{
		isEmpty = true;
		hasFloor = false;
		for (int i = 0; i < 3; i++)
		{
			int num = 0;
			while (num < 3)
			{
				int i2 = topLeftX + i;
				int j = topLeftY + num;
				if (!SolidTile(i2, j))
				{
					num++;
					continue;
				}
				goto IL_001e;
			}
			continue;
			IL_001e:
			isEmpty = false;
			break;
		}
		for (int k = 0; k < 3; k++)
		{
			int i3 = topLeftX + k;
			int j2 = topLeftY + 3;
			if (SolidTile(i3, j2))
			{
				hasFloor = true;
				break;
			}
		}
	}

