	private static void PrefixItemFromOptions(Item item, int[] options)
	{
		byte prefix = item.prefix;
		if (!item.Prefix(-3))
		{
			return;
		}
		List<int> list = new List<int>(options);
		while (list.Count > 0)
		{
			int index = _genRand.Next(list.Count);
			int num = list[index];
			item.Prefix(num);
			if (item.prefix == num)
			{
				return;
			}
			list.RemoveAt(index);
		}
		item.Prefix(prefix);
	}

