	public static void ClearBrokenTraps(Point startTileCoords, List<Point> pointsWeAlreadyWentOver)
	{
		List<Point> t = new List<Point>();
		List<Point> t2 = new List<Point>();
		List<Point> list = new List<Point>();
		bool flag = false;
		bool flag2 = false;
