		{
			Utils.Swap(ref t, ref t2);
			while (t.Count > 0)
			{
				Point item = t[0];
				t.RemoveAt(0);
				if (!InWorld(item.X, item.Y, 5))
				{
					continue;
				}
				Tile tile = Main.tile[item.X, item.Y];
