					if (!list.Contains(item2))
					{
						t2.Add(item2);
					}
					item2 = new Point(item.X, item.Y + 1);
					if (!list.Contains(item2))
					{
						t2.Add(item2);
					}
				}
			}
			if (flag2 && flag)
			{
				break;
			}
		}
		if (flag2 && flag)
		{
			return;
		}
		foreach (Point item3 in list)
		{
			Tile tile2 = Main.tile[item3.X, item3.Y];
