			num -= 40;
			num2++;
		}
		if (num == 18)
		{
			y--;
		}
		Vector2 vector = new Vector2(x * 16 + 8, y * 16 + 4);
		int type = 167 + num2;
		int damage = 150;
		int num3 = 7;
		int num4 = Projectile.NewProjectile(GetProjectileSource_PlayerOrWires(x, y, fromWiring, Main.LocalPlayer), vector.X, vector.Y + 2f, 0f, -8f, type, damage, num3, Main.myPlayer);
