		KillTile_DropItems(x, y, tileSafely, includeLargeObjectDrops: true);
		AttemptFossilShattering(x, y, tileSafely, fail: false);
		ReplaceTIle_DoActualReplacement(targetType, targetStyle, x, y, tileSafely);
