	public static bool IsOpenDoorAnchorFrame(int x, int y)
	{
		Tile tile = Main.tile[x, y];
		if (!tile.active() || tile.type != 11)
		{
			return false;
		}
		int num = tile.frameX % 72;
		if (num >= 18)
		{
			return num >= 54;
		}
		return true;
	}

